  `mainpost_id` int(11) NOT NULL AUTO_INCREMENT,
  `mainpost_tittle` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  `mainpost_content` mediumtext COLLATE utf8_unicode_ci,
  `mainpost_desc` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  `mainpost_time` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  `mainpost_userid` int(11) DEFAULT NULL,
  `mainpost_username` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  `mainpost_type` int(11) DEFAULT NULL,
  PRIMARY KEY (`mainpost_id`),
  UNIQUE KEY `mainpost_id_UNIQUE` (`mainpost_id`),
  KEY `mainpost_user_idx` (`mainpost_userid`),
  CONSTRAINT `mainpost_user` FOREIGN KEY (`mainpost_userid`) REFERENCES `user` (`userId`) ON DELETE RESTRICT ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
