--
-- Table structure for table `news`
--

DROP TABLE IF EXISTS `news`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `news` (
  `newsId` int(11) NOT NULL AUTO_INCREMENT,
  `newsTittle` varchar(45) COLLATE utf8_unicode_ci NOT NULL,
  `newsContent` longtext COLLATE utf8_unicode_ci NOT NULL,
  `newsData` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  `newsDesc` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `newsIsTop` int(11) DEFAULT NULL,
  `newsImagePath` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`newsId`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

